class RepeatedValuesDelegate(QtWidgets.QItemDelegate):
    """Custom delegate for highlighting repeated values in a table.

    Inherits from QItemDelegate and modifies the painting behavior for repeated values.
    """
    def __init__(self, parent=None):
        """Initializes the repeated values delegate.

        Args:
            parent: The parent widget or object.
        """
        super().__init__(parent)
    
    def paint(self, painter, option, index):
        """Customizes the painting of table cells to highlight repeated values.

        Args:
            painter (QPainter): The painter object used for drawing.
            option (QStyleOptionViewItem): The style options for the item.
            index (QModelIndex): The model index of the item to paint.
        """
        model = index.model()
        value = model.data(index)
        
        background_color = QtGui.QColor(255, 255, 255, 0)
        for row in range(model.rowCount()):
            if row != index.row():
                other_index = model.index(row, index.column())
                if model.data(other_index) == value:
                    background_color = QtGui.QColor(255, 0, 0, 100)
                    break
        
        painter.fillRect(option.rect, background_color)
        option.displayAlignment = QtCore.Qt.AlignmentFlag.AlignCenter

        super().paint(painter, option, index)

