    def ExportOffer_Form(self):
        numoffer = self.NumOffer_ExportOffer_Form.text()
        rev = self.Revision_ExportOffer_Form.text()
        validity = self.Validity_ExportOffer_Form.text()
        delivery_time = self.DeliveryTime_ExportOffer_Form.text()
        delivery_term = self.DeliveryTerm_ExportOffer_Form.text()
        testinspection = self.TestInspection_ExportOffer_Form.text()
        project = self.Project_ExportOffer_Form.text()
        revchanges = self.RevChanges_ExportOffer_Form.text()
        notes = self.Notes_ExportOffer_Form.toPlainText()

        if self.radio100.isChecked()==True:
            pay_term = '100'
        elif self.radio90_10.isChecked()==True:
            pay_term = '90_10'
        elif self.radio50_50.isChecked()==True:
            pay_term = '50_50'
        elif self.radioOthers.isChecked()==True:
            pay_term = 'Others'
        else:
            pay_term = 'Not Value'

        if any(value  in ['None',''] for value in [validity, delivery_time, delivery_term, testinspection, project]):
            dlg = QtWidgets.QMessageBox()
            new_icon = QtGui.QIcon()
            new_icon.addPixmap(QtGui.QPixmap(os.path.abspath(os.path.join(basedir, "Resources/Iconos/icon.ico"))), QtGui.QIcon.Mode.Normal, QtGui.QIcon.State.Off)
            dlg.setWindowIcon(new_icon)
            dlg.setWindowTitle("Exportar Oferta")
            dlg.setText('Los campos "Validez", "T. Entrega", "Cond. Entrega" y "Test & Inspec." no puede ser "None" ni estar vacíos')
            dlg.setIcon(QtWidgets.QMessageBox.Icon.Warning)
            dlg.exec()
            del dlg, new_icon

        elif pay_term == 'Not Value':
                dlg = QtWidgets.QMessageBox()
                new_icon = QtGui.QIcon()
                new_icon.addPixmap(QtGui.QPixmap(os.path.abspath(os.path.join(basedir, "Resources/Iconos/icon.ico"))), QtGui.QIcon.Mode.Normal, QtGui.QIcon.State.Off)
                dlg.setWindowIcon(new_icon)
