    def queryofferdata(self):
        """
        Queries the database for offer data based on the offer number provided by the user. It displays the retrieved information 
        in the corresponding form fields.
        """
        numoffer=self.NumOffer_ExportOffer_Form.text()
    #SQL Query for loading existing data in database
        commands_loaddataoffer = ("""
                    SELECT "validity","delivery_time","delivery_term","project"
                    FROM offers
                    WHERE "num_offer" = %s
                    """)
        conn = None
        try:
        # read the connection parameters
            params = config()
        # connect to the PostgreSQL server
            conn = psycopg2.connect(**params)
            cur = conn.cursor()
        # execution of commands one by one
            cur.execute(commands_loaddataoffer,(numoffer,))
            results=cur.fetchall()
        # close communication with the PostgreSQL database server
            cur.close()
        # commit the changes
            conn.commit()

            self.Validity_ExportOffer_Form.setText(str(results[0][0]))
            self.DeliveryTime_ExportOffer_Form.setText(str(results[0][1]))
            self.DeliveryTerm_ExportOffer_Form.setText(str(results[0][2]))
            self.Project_ExportOffer_Form.setText(str(results[0][3]))

        except (Exception, psycopg2.DatabaseError) as error:
            dlg = QtWidgets.QMessageBox()
            new_icon = QtGui.QIcon()
            new_icon.addPixmap(QtGui.QPixmap(os.path.abspath(os.path.join(basedir, "Resources/Iconos/icon.ico"))), QtGui.QIcon.Mode.Normal, QtGui.QIcon.State.Off)
            dlg.setWindowIcon(new_icon)
            dlg.setWindowTitle("ERP EIPSA")
            dlg.setText("Ha ocurrido el siguiente error:\n"
                        + str(error))
            dlg.setIcon(QtWidgets.QMessageBox.Icon.Critical)
            dlg.exec()
            del dlg, new_icon
        finally:
            if conn is not None:
                conn.close()


