    def editorder(self):
        """
        Edit the corresponding entry in database after validating form inputs.
        """
        numorder=self.NumOrder_EditOrder.text()
        numoffer=self.NumOffer_EditOrder.text()
        numref=self.NumRef_EditOrder.text()
        expectdate=self.ExpectDate_EditOrder.text() if self.ExpectDate_EditOrder.text() != '' else None
        notes=self.Notes_EditOrder.toPlainText()
        amount=self.Amount_EditOrder.text()
        num_items=self.NumItems_EditOrder.text() if self.NumItems_EditOrder.text() != '' else '0'
        amount=amount.replace(".",",")

        #SQL Query for checking if order number exists in database
        commands_checkorder = ("""
                    SELECT * 
                    FROM orders
                    WHERE "num_order" = %s
                    """)

        try:
            with Database_Connection(config()) as conn:
                with conn.cursor() as cur:
                    cur.execute(commands_checkorder,(self.original_numorder,))
                    results=cur.fetchall()
                    match=list(filter(lambda x:self.original_numorder in x, results))

        except (Exception, psycopg2.DatabaseError) as error:
            MessageHelper.show_message("Ha ocurrido el siguiente error:\n"
                        + str(error), "critical")

        if numorder=="" or (numorder==" " or len(match)==0):
            MessageHelper.show_message("Introduce un número de pedido válido", "warning")

        elif numorder== "" or (numoffer== "" or (numref== "" or (expectdate=="" or amount==""))):
            MessageHelper.show_message("Los campos no pueden estar vacíos", "warning")

        else:
            #SQL Query for updating values in database
            commands_editorder = ("""
                        UPDATE orders
                        SET "num_order" = %s, "num_offer" = %s, "num_ref_order" = %s, "expected_date" = %s, "notes" = %s, "order_amount" = %s, "items_number" = %s
                        WHERE "num_order" = %s
                        """)

            try:
                with Database_Connection(config()) as conn:
                    with conn.cursor() as cur:
                        data=(numorder,numoffer,numref,expectdate,notes,amount,num_items,self.original_numorder,)
                        cur.execute(commands_editorder,data)
                    conn.commit()

            except (Exception, psycopg2.DatabaseError) as error:
                MessageHelper.show_message("Ha ocurrido el siguiente error:\n"
                            + str(error), "critical")

            MessageHelper.show_message("Pedido editado con exito", "info")

            self.NumOrder_EditOrder.setText('')
            self.NumOffer_EditOrder.setText('')
            self.NumRef_EditOrder.setText('')
            self.ExpectDate_EditOrder.setText('')
            self.Notes_EditOrder.setText('')
            self.Amount_EditOrder.setText('')
            self.NumItems_EditOrder.setText('')


    def queryorderdata(self):
        """
        Queries the database for order data based on the order number provided by the user. It displays the retrieved information 
        in the corresponding form fields.
        """
        self.original_numorder=self.NumOrder_EditOrder.text()
    #SQL Query for loading existing data in database
        commands_loaddataorder = ("""
                    SELECT "num_order","num_offer","num_ref_order",TO_CHAR("expected_date", 'DD-MM-YYYY'),"notes",
                                CAST("order_amount" AS numeric) AS "amount","items_number", "regularisation"
                    FROM orders
                    WHERE "num_order" = %s
                    """)

        try:
            with Database_Connection(config()) as conn:
                with conn.cursor() as cur:
                    cur.execute(commands_loaddataorder,(self.original_numorder,))
                    results=cur.fetchall()
                    match=list(filter(lambda x:self.original_numorder in x, results))

        except (Exception, psycopg2.DatabaseError) as error:
            MessageHelper.show_message("Ha ocurrido el siguiente error:\n"
                        + str(error), "critical")

        if len(match)==0:
            MessageHelper.show_message("El número de pedido introducido no existe", "warning")

        else:
            self.NumOffer_EditOrder.setText(str(results[0][1]))
            self.NumRef_EditOrder.setText(str(results[0][2]))
            self.ExpectDate_EditOrder.setText(str(results[0][3]) if str(results[0][3]) != 'None' else '')
            self.Notes_EditOrder.setText(str(results[0][4]))
            self.Amount_EditOrder.setText(str(results[0][5]))
            self.NumItems_EditOrder.setText(str(results[0][6]))
            self.regularisation = (str(results[0][7]) if str(results[0][7]) != 'None' else '')

    def show_dialog_regularisation(self):
        """
        Displays a dialog window allowing the user to view and edit regularisation.
        The dialog is only shown if the Offer number is not empty.
        """
        if self.NumOrder_EditOrder.text() != '':
            dlg = QtWidgets.QDialog()
            dlg.setWindowTitle("Dialog")
            dlg.setGeometry(50, 50, 900, 600)
            dlg.setWindowModality(QtCore.Qt.WindowModality.NonModal)

            vLayout = QtWidgets.QVBoxLayout(dlg)

            btn = QtWidgets.QPushButton("Guardar")
            vLayout.addWidget(btn)
            btn.clicked.connect(self.save_regularisation)
            
            self.te = QtWidgets.QTextEdit()
            self.te.setText(self.regularisation)
            self.te.setStyleSheet("background-color: transparent;")
            vLayout.addWidget(self.te)

            dlg.show()

            self.dialog = dlg

    def save_regularisation(self):
        """
        Saves the order regularisation to the database.
        """
        num_order = self.NumOrder_EditOrder.text()
        self.regularisation = self.te.toPlainText()

        commands_update_regularisation = ("""
                        UPDATE orders
                        SET "regularisation" = %s
                        WHERE "num_order" = %s
                        """)

        try:
            with Database_Connection(config()) as conn:
                with conn.cursor() as cur:
                    data=(self.regularisation, num_order,)
                    cur.execute(commands_update_regularisation, data)
                conn.commit()

        except (Exception, psycopg2.DatabaseError) as error:
            MessageHelper.show_message("Ha ocurrido el siguiente error:\n"
                        + str(error), "critical")




