    def query_tags(self):
        numorder=self.Numorder_QueryTags.text()
        numoffer=self.Numoffer_QueryTags.text()

        if ((numorder=="" or numorder==" ") and (numoffer=="" or numoffer==" ")):
            dlg = QtWidgets.QMessageBox()
            new_icon = QtGui.QIcon()
