    def update_coms(self):
        order_id=self.label_IDOrd.text()
        order_com=self.Coms_SupplierOrder.toPlainText()

        if order_id=="":
            dlg = QtWidgets.QMessageBox()
            new_icon = QtGui.QIcon()
