            sum_value = sum([self.euro_string_to_float(str(ix.data())) if re.match(r'^[\d.,]+\s$', str(ix.data())) else float(str(ix.data()).replace(',', '.')) if str(ix.data()).replace(',', '.').replace('.', '', 1).isdigit() else 0 for ix in self.tableDimDwg.selectedIndexes()])
            count_value = len([ix for ix in self.tableDimDwg.selectedIndexes() if ix.data() != ""])
            if sum_value > 0:
                self.label_SumItems.setText("Suma:")
